package com.assignment_23_05_23;

public class NoOfOdds {
//17.Calculate No. of odd Nums between 1-20
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// TODO Auto-generated method stub
		int count=0;
		for(int i=1;i<=20;i++)
		{
			if(i%2!=0)
				count++;
		}
		System.out.println(count);
	
	}

}
